mapper.select:
    SELECT
      COUNT(1) AS sum
    FROM
      tb_recevice_file r, tb_dept d
    
    	r.deptId = d.deptId
    	
			and keyWords like '%${keyWords}%'
		
		
			and fileTitle = #{fileTitle, jdbcType=VARCHAR}
		
		
			and fileNo = #{fileNo, jdbcType=VARCHAR}
		
		
			and secretLevel = #{secretLevel, jdbcType=VARCHAR}
		
		
			and emgLevel = #{emgLevel, jdbcType=VARCHAR}
		
		
			and status = #{status, jdbcType=VARCHAR}
		
		
			and fileId = #{fileId, jdbcType=VARCHAR}
		
		
				
					and createDate >= DATE_FORMAT(#{startDate, jdbcType=VARCHAR}, '%Y-%m-%d')
				
			
			
				
					and createDate < DATE_FORMAT(#{endDate, jdbcType=VARCHAR}, '%Y-%m-%d')

 	SELECT
 		d.deptName,   
    	
    FROM
      tb_recevice_file r, tb_dept d
    
    	r.deptId = d.deptId
    	
			 and keyWords like '%${keyWords}%'
		
		
			and fileTitle = #{fileTitle, jdbcType=VARCHAR}
		
		
			and fileNo = #{fileNo, jdbcType=VARCHAR}
		
		
			and secretLevel = #{secretLevel, jdbcType=VARCHAR}
		
		
			and emgLevel = #{emgLevel, jdbcType=VARCHAR}
		
		
			and status = #{status, jdbcType=VARCHAR}
		
		
			and fileId = #{fileId, jdbcType=VARCHAR}
		
		
			
				and createDate >= DATE_FORMAT(#{startDate, jdbcType=VARCHAR}, '%Y-%m-%d')
			
		
		
			
				and createDate <= DATE_FORMAT(#{endDate, jdbcType=VARCHAR}, '%Y-%m-%d')
			
		
    
    order by fileId
	limit #{startRow}, #{endRow}
