mapper.select:
    SELECT
      COUNT(1) AS sum
    FROM
      tb_send_file
    
    	
				 keyWords like '%${keyWords}%'
			
			
				and fileTitle = #{fileTitle, jdbcType=VARCHAR}
			
			
				and fileNo = #{fileNo, jdbcType=VARCHAR}
			
			
				and secretLevel = #{secretLevel, jdbcType=VARCHAR}
			
			
				and author = #{author, jdbcType=VARCHAR}
			
			
				and checkAuthor = #{checkAuthor, jdbcType=VARCHAR}
			
			
				and signAuthor = #{signAuthor, jdbcType=VARCHAR}
			
			
				and fileId = #{fileId, jdbcType=VARCHAR}
			
			
	           	
	       			AND  createDate >= TO_DATE(#{startDate, jdbcType=VARCHAR},'yyyyMMdd')
	           	
	       	
	   	    
	           	
	       			AND  createDate < TO_DATE(#{endDate, jdbcType=VARCHAR},'yyyyMMdd')

 	SELECT   
    	
    FROM
      tb_send_file
    
    	
			 keyWords like '%${keyWords}%'
		
		
			and fileTitle = #{fileTitle, jdbcType=VARCHAR}
		
		
			and fileNo = #{fileNo, jdbcType=VARCHAR}
		
		
			and secretLevel = #{secretLevel, jdbcType=VARCHAR}
		
		
			and author = #{author, jdbcType=VARCHAR}
		
		
			and checkAuthor = #{checkAuthor, jdbcType=VARCHAR}
		
		
			and signAuthor = #{signAuthor, jdbcType=VARCHAR}
		
		
			and fileId = #{fileId, jdbcType=VARCHAR}
		
    
    order by fileId
	limit #{startRow}, #{endRow}
