mapper.select:
SELECT COUNT(1) AS sum FROM tb_warrant
borrower like '%${borrower}%'
and id = #{id, jdbcType=VARCHAR}
and status = #{status, jdbcType=INTEGER}
and createDate >= DATE_FORMAT(#{startDate, jdbType=VARCHAR}, '%Y-%m-%d')
and createDate <= DATE_FORMAT(#{endDate, jdbType=VARCHAR}, '%Y-%m-%d')

 	SELECT   
    	
    FROM
      tb_warrant
      
    	
			 borrower like '%${borrower}%'
		
		
			and id = #{id, jdbcType=VARCHAR}
		
		
			and status = #{status, jdbcType=INTEGER}
		
		
			
				and createDate >= DATE_FORMAT(#{startDate, jdbType=VARCHAR}, '%Y-%m-%d')
			
		
		
			
				and createDate <= DATE_FORMAT(#{endDate, jdbType=VARCHAR}, '%Y-%m-%d')
			
		
    
    order by id desc
	limit #{startRow}, #{endRow}
